vector<int> edges[MAX_N];
bool check[MAX_N];

int dfs( int u ) {
    check[u] = 1;
    int ans = 0, num_f = 0;
    for ( int v : edges[u] )
        if ( !check[v] ) {
            ans ^= 1;
            num_f++;
            ans ^= dfs( v );
        }
    if ( num_f == 1 )
        return 1;
    return ans;
}

