void solve() {
    //constexpr i64 mod = 1'000'000'007;
    i64 N;
    cin >> N;
    vector<vector<i64>> tree(N);
    rep(i,0,N-1){
        i64 x,y;
        cin >> x >> y;
        --x;
        --y;
        tree[x].emplace_back(y);
        tree[y].emplace_back(x);
    }

    function<i64(i64,i64)> rec=[&](i64 u,i64 p){
        i64 g=0;
        for(i64 v:tree[u]){
            if(v==p) continue;
            g^=rec(v,u)+1;
        }
        return g;
    };

    cout << (rec(0,-1)!=0?"Alice":"Bob") << endl;
}

