void orient(int nd){
	for (auto x: A[nd].nei) if (x!=A[nd].par){
		A[nd].children.push_back(x);
		A[x].par = nd;
		orient(x);
	}
}

void sg(int nd){
	int ret = 0;
	for (auto x: A[nd].children){
		sg(x);
		ret ^= A[x].sg;
	}
	ret++;
	A[nd].sg = ret;
	return;
}
