#include <bits/stdc++.h>

#define pb push_back
#define mp make_pair
#define size(n) ( int( n.size() ) )
#define sqr(n) ( (n) * (n) )

using namespace std;

typedef long long ll;
typedef long double ld;

const int N = 1e5 + 5;
vector < int > g[N];
int parent[N];
ll f[N], res = 0;

void dfs( int v ){
    if ( ( v != 1 ) && ( size(g[v]) == 1 ) ){
        f[v] = 1;
        return;
    }
    ll pr = 0, sf = 0;
    for ( int j = 0; j < size(g[v]); j++ ){
        int u = g[v][j];
        if ( u != parent[v] ){
            parent[u] = v;
            dfs(u);
            sf ^= f[u];
        }
    }
    if ( v == 1 ){
        res = sf;
    }
    vector < pair < int, int > > s;
    ll e, sum = 0;
    for ( int j = 0; j < size(g[v]); j++ ){
        int u = g[v][j];
        if ( u != parent[v] ){
            sf ^= f[u];
            e = ( sf ^ pr ^ 1 );
            sum = 0;
            for ( int i = 60; i >= 0; i-- ){
                int bitU = ( f[u] >> i ) & 1LL;
                int bitE = ( e >> i ) & 1LL;
                if ( bitU == 1 ){
                    if ( bitE == 0 ){
                        s.pb( mp( sum, sum + ( 1LL << i ) - 1 ) );
                    }
                    else{
                        s.pb( mp( sum + ( 1LL << i ), sum + ( 1LL << i ) - 1 + ( 1LL << i ) ) );
                    }
                }
                sum += ( 1LL << i ) * ( bitE ^ bitU );
            }
            pr ^= f[u];
        }
    }
    sort( s.begin(), s.end() );

    int l = 0;
    for ( int i = 0; i < size(s); i++ ){
        int cl = s[i].first;
        int cr = s[i].second;
        if ( cl > l + 1 ){
            break;
