    auto res = Dfs(u, v);
    if (res.X == -1) {
      first = false;
      cnt++;
    }
  }

  pii ret(1-cnt%2, cnt%2);
  bool spe = false;
  for (int u : es[v]) {
    if (u == p) continue;

    auto res = Dfs(u, v);
    if (res.X == -1) continue;

    if (first) {
      ret.X = 0;
      ret.Y = res.X;
      if (res.X == 0) spe = true;
    } else {
      if (ret.X == 1) ret.X = 0;
      else ret.X = res.Y;

      if (ret.Y == 1) {
        assert(!spe);
        ret.Y = 0;
      } else if (spe) {
        assert(ret.Y == 0);
        ret.Y = !res.X;
      } else {
        ret.Y = res.X;
      }
      spe = false;
    }
    first = false;
  }
  //printf("%d: %d, %d\n", v+1, ret.X, ret.Y);
  return memo[v] = ret;
}

