void add_edge(int u, int v) {
    gto[e] = v; gnxt[e] = adj[u]; adj[u] = e++;
}

void dfs(int s) {
    size[s] = 1;
    if (!~adj[s]) {
        return;
    }
    for (int e = adj[s]; ~e; e = gnxt[e]) {
        if (gto[e] != pre[s]) {
            pre[gto[e]] = s;
            dfs(gto[e]);
            size[s] += size[gto[e]];
        }
    }
}

int dfs2(int s) {
    if (!~adj[s]) {
        return nim[s] = 0;
    }
    for (int e = adj[s]; ~e; e = gnxt[e])
        if (gto[e] != pre[s]) {
            nim[s] ^= dfs2(gto[e]) + 1;
        }
    return nim[s];
}

