    scanf("%d", &N);
    for (int i = 1; i != N; ++i) {
        int x, y; scanf("%d%d", &x, &y);
        add_edge(x, y); add_edge(y, x);
    }
    dfs(1); dfs2(1);
    puts(nim[1] ? "Alice" : "Bob");
    return 0;
}

