#include<bits/stdc++.h>
using namespace std;
typedef long long ll;

class UnionFind{
    public:
    //-()
    vector<int> parent;

    //-1
    UnionFind(int N){
        parent = vector<int>(N,-1);
    }

    //A(A)
    int root(int A){
        if(parent[A] < 0) return A;
        return parent[A]=root(parent[A]);
    }

    //
    int size(int A){
        return -parent[root(A)];
    }

    //AB
    bool unite(int A, int B) {
    //ABroot(A)root(B)
        A = root(A);
        B = root(B);
        //
        if(A == B) return false; 

        //(A)(B)
        //
        if(size(A) < size(B)) swap(A,B);

        //A
        parent[A] += parent[B];
        //BA
        parent[B] = A;

        return true;
    }

    //ABtrue
    bool same(int A, int B){
        return root(A)==root(B);
    } 
};

vector<vector<int>> v(100010);
bool used[100010];

int dfs(int p){
    used[p] = true;
    int x = 0;
    for(auto i:v[p]){
        if(!used[i]){
            x ^= dfs(i) + 1;
        }
    }
    return x;
}

