vector<int> dp;
int dfs(int v, int p, Graph<int> &G) {
    for (auto nv : G[v]) {
        if (nv == p)
            continue;
        dp[v] ^= dfs(nv, v, G) + 1;
    }
    return dp[v];
}

int main() {
