void bfs(int from) {
    Grundy[from] = 0;
    int child = 0;
    for(int i = 0; i < pathes[from].size(); i++) {
        int to = pathes[from][i];
        if(to == parent[from]) continue;
        parent[to] = from;
        bfs(to);
        Grundy[from] ^= Grundy[to];
        child++;
    }
    if(child == 1) Grundy[from]++;
    return;
}

