int grundy(int i=0) {
    int ans = 0;
    for (auto g:G[i]) {
        if (childs[g]) continue;
        childs[i]++;
        par[g] = i;
        if (i > 0) ans += grundy(g);
        else ans ^= grundy(g);
    }
    if (i > 0) {
        if (childs[i] > 1) ans = 0;
        ans++;
    }
    return ans;
}

