using Graph=vector<vector<int>>;

int grundy(int v,int pre,Graph& g){
    vector<int> gs;
    for(auto to:g[v]){
        if(to!=pre) gs.push_back(grundy(to,v,g)+1);
    }
    int res=0;
    for(auto g:gs) res^=g;
    return res;
} 
