void Add(int i,int j)
{
    tot++;
    edge[tot].num=j;
    edge[tot].next=last[i];
    last[i]=tot;
}

void dfs_Init(int i)
{
    for (int k=last[i];k!=0;k=edge[k].next)
        {
        if (edge[k].num==fa[i]) continue;
        fa[edge[k].num]=i;
        num[i]++;
        dfs_Init(edge[k].num);
        }
}

void dfs_work(int i)
{
    if (num[i]==0)
        {
        sg[i]=0;
        return ;
        }
    int sum=0;;
    for (int k=last[i];k!=0;k=edge[k].next)
        {
        if (edge[k].num==fa[i]) continue;
        dfs_work(edge[k].num);
        sum=sum^(sg[edge[k].num]+1);
        }
    sg[i]=sum;
}

