};

int N;
Graph g_;
Graph g;

ll grundy(int root)
{
    if (g.edge[root].empty()) {
        return 1;
    } else if (g.edge[root].size() == 1) {
        return grundy(g.edge[root][0]) + 1;
    } else {
        ll num = 0;
        for (const int p : g.edge[root]) {
            num ^= grundy(p);
        }
        return num + 1;
    }
}

