    }
    bool check[MAX];
    fill(check, check + MAX, false);
    queue<int> q;
    q.push(0);
    while (not q.empty()) {
        const int p = q.front();
        q.pop();
        check[p] = true;
        for (const int to : g_.edge[p]) {
            if (not check[to]) {
                g.addEdge(p, to);
                q.push(to);
            }
        }
    }

    const ll grundynum = grundy(0);
    if (grundynum == 1) {
        cout << "Bob" << endl;
    } else {
        cout << "Alice" << endl;
    }

    return 0;
}

