#include <bits/stdc++.h>

const int N = 100005, LN = 50;
int tc, n, m, idx, lc[N * LN], rc[N * LN], sum[N * LN], tag[N * LN], x, y;
int head[N], tot;
struct edge { int to, nxt; } e[N << 1];
void link(int x, int y) {
	e[++tot] = (edge) { y, head[x] }, head[x] = tot;
	e[++tot] = (edge) { x, head[y] }, head[y] = tot;
}
void pushdown(int root, int dep) {
	if (tag[root] >> dep & 1) std::swap(lc[root], rc[root]);
	tag[lc[root]] ^= tag[root], tag[rc[root]] ^= tag[root], tag[root] = 0;
}
void insert(int &root, int val, int dep) {
	if (!root) root = ++idx;
	if (dep == -1) { sum[root] = 1; return; }
	pushdown(root, dep);
	if (val >> dep & 1) insert(rc[root], val, dep - 1);
	else insert(lc[root], val, dep - 1);
	sum[root] = sum[lc[root]] + sum[rc[root]];
}
int query_mex(int root, int dep) {
	if (!root) return 0;
	pushdown(root, dep);
	if (sum[lc[root]] >> dep)
		return query_mex(rc[root], dep - 1) | 1 << dep;
	else
		return query_mex(lc[root], dep - 1);
}
int merge(int x, int y, int dep) {
	if (dep == -1 || !x || !y) return x ?: y;
	pushdown(x, dep), pushdown(y, dep);
	lc[x] = merge(lc[x], lc[y], dep - 1);
	rc[x] = merge(rc[x], rc[y], dep - 1);
	sum[x] = sum[lc[x]] + sum[rc[x]];
	return x;
}
bool vis[N];
int root[N], sg[N], f[N];
void dfs(int x) {
	vis[x] = 1; int s = 0;
	for (int i = head[x]; i; i = e[i].nxt)
		if (!vis[e[i].to]) f[e[i].to] = x, dfs(e[i].to), s ^= sg[e[i].to];
	insert(root[x], s, 16);
	for (int i = head[x]; i; i = e[i].nxt)
		if (f[e[i].to] == x) {
			tag[root[e[i].to]] ^= s ^ sg[e[i].to];
			root[x] = merge(root[x], root[e[i].to], 16);
		}
	sg[x] = query_mex(root[x], 16);
}
void solve() {
	std::cin >> n, idx = tot = 0; m = n - 1;
	for (int i = 1; i <= n; ++i) head[i] = f[i] = vis[i] = root[i] = 0;
	for (int i = 1; i <= m; ++i) std::cin >> x >> y, link(x, y);
	int s = 0;
	for (int i = 1; i <= n; ++i) if (!vis[i]) dfs(i), s ^= sg[i];
	for (int i = 0; i <= idx; ++i) lc[i] = rc[i] = sum[i] = tag[i] = 0;
	std::cout << (s ? "Alice" : "Bob") << '\n';
}
int main() {
	std::ios::sync_with_stdio(0), std::cin.tie(0);
	tc = 1;
	while (tc--) solve();
	return 0;
}

