    S ll parent[100005];
    S V<ll> children[100005];
    S ll level[100005];
    make_parental_relation(linklist, 1, n,
                            parent, children, level);

    S ll grundy[100005];
    memset(grundy, 0, sizeof(grundy));

    ll childcnt[100005];
    rep (i, 100005) childcnt[i] = children[i].size();

    S queue<ll> q;
    srep (v, 1, n+1) if (!childcnt[v]) q.push(v);

    while (!q.empty()) {
        ll v = q.front(); q.pop();
        ll pv = parent[v];
        if (v == pv) continue;
        ll g = grundy[v] + 1LL;
        grundy[pv] ^= g;
        childcnt[pv]--;
        if (!childcnt[pv]) q.push(pv);
    }

    cout << ((grundy[1]) ? "Alice" : "Bob") << endl;
}

