void calc_grundy(ll now, vector<vector<ll>>& graph, vector<ll>& grundy, vector<bool>& visited) {
    visited[now] = true;
    ll g = 0;
    for (ll to : graph[now]) {
        if (visited[to]) {
            continue;
        }
        calc_grundy(to, graph, grundy, visited);
        g = g ^ (grundy[to] + 1);
    }
    grundy[now] = g;
}

