int solve(int cur, int par) { //Returns 0 if the first player wins, 1 if the second player wins.
	//Solve the children

	//cout << "solving " << cur << ' ' << par << endl;

	int childCount = adj[cur].size() - (par == -1 ? 0 : 1);

	if (childCount == 1) {
		return 0; //First player moves and wins.
	} else if (childCount == 0) {
		return 1;
	}

	int zeroes = 0;
	int ones = 0;

	for (int next : adj[cur]) {
		if (next == par) continue;

		//cout << "solving " << next << " got " << solve(next, cur) << endl;
		if (solve(next, cur)) {
			ones++;
		} else {
			zeroes++;
		}
	}

	//If for example we have two zeroes, result is 1. (first player picks child, second player picks, second wins.)
	//If we have a zero and a one , result is 0


	if (ones % 2 == 0 && zeroes % 2 == 0) {
		return 1;
	} else if (ones % 2 == 0 && zeroes % 2 == 1) {
		return 0;
	} else if (ones % 2 == 1 && zeroes % 2 == 0) {
		return 0;
	} else if (ones % 2 == 1 && zeroes % 2 == 1) {
		return 0;
	}
	//should never be reached
	assert(false);
	return 1;
}

