void make_edgelist(const V<t2> &srclist, V<ll> edgelist[])
{
    for (auto src : srclist) {
        ll a = get<0>(src);
        ll b = get<1>(src);
        edgelist[a].pb(b);
        edgelist[b].pb(a);
    }
}

void make_parental_relation(const V<ll> edgelist[], ll root, ll n,
                            ll parents[], V<ll> children[], ll levels[])
{
    queue<ll> q;
    bool checked[n+1];

    memset(checked, 0, sizeof(checked));

    q.push(root);
    checked[root] = true;
    parents[root] = root;
    levels[root] = 0LL;

    for ( ; !(q.empty()); ) {
        ll now = q.front(); q.pop();
        for (auto next : edgelist[now]) {
            if (checked[next]) continue;
            q.push(next);
            checked[next] = true;
            parents[next] = now;
            children[now].pb(next);
            levels[next] = levels[now] + 1LL;
        }
    }
}

void _main()
{
    ll n; llin(n);
    V<t2> uvlist; llinl2(uvlist, n-1);

    S V<ll> edgelist[100005];
    make_edgelist(uvlist, edgelist);

    S ll parents[100005];
    S V<ll> children[100005];
    S ll levels[100005];
    make_parental_relation(edgelist, 1, n, parents, children, levels);

    S ll childcnts[100005];
    srep (v, 1, n+1) childcnts[v] = children[v].size();

    S ll grundy[100005] = {};
    queue<ll> q;
    srep (v, 1, n+1) {
        if (!childcnts[v]) q.push(v);
    }

    while (q.size()) {
        ll v = q.front(); q.pop();
        ll pv = parents[v];
        if (v == pv) continue;
        grundy[pv] ^= (grundy[v] + 1LL);
        childcnts[pv]--;
        if (!childcnts[pv]) q.push(pv);
    }

    cout << (grundy[1] ? "Alice" : "Bob") << endl;
}

