mat graph;
vec status;

ll dfs(ll v, ll from) {
    // cout << v << "\n";
    if (status[v] != -1) return status[v];
    if (from != -1 and graph[v].size() == 1) {
        status[v] = 0;
        return 0;
    }

    ll zero = 0, one = 0;
    for (ll nv : graph[v]) {
        if (nv == from) continue;
        if (dfs(nv, v) == 0) zero++;
        else one++;
    }

    if ((zero + one) % 2 == 1) {
        status[v] = 1;
        return 1;
    } else if (one % 2 == 1) {
        status[v] = 1;
        return 1;
    } else {
        return 0;
    }
}

