namespace Problem {
using namespace std;

class Solver {
 public:
  int n;
  vector<vector<int>> t;
  vector<int> grundy;
  Solver(LL n) : n(n), t(n), grundy(n){};

  void solve() {
    for (int i = 0; i < n - 1; ++i) {
      int a, b;
      cin >> a >> b;
      t[--a].push_back(--b);
      t[b].push_back(a);
    }
    dfs(0, -1);
    cout << (grundy[0] ? "Alice" : "Bob") << endl;
  }
  void dfs(int c, int p) {
    for (auto v : t[c]) {
      if (v != p) {
        dfs(v, c);
        grundy[c] ^= (grundy[v] + 1);
      }
    }
  }
};

}  // namespace Problem

int main() {
  std::cin.tie(0);
  std::ios_base::sync_with_stdio(false);
  // std::cout << std::fixed << std::setprecision(12);
  long long n = 0;
  std::cin >> n;

  Problem::Solver sol(n);
  sol.solve();
  return 0;
}

