std::vector<std::vector<int>> g;
std::vector<int> dp;
int n;

int dfs(int v, int p = -1){
	if(~dp[v]) return dp[v];

	int ret = 0;
	for(auto e : g[v]){
		if(e == p) continue;
		ret ^= dfs(e, v) + 1;
	}

	return dp[v] = ret;
}

