int kai_size = 400001;
vel kai(kai_size, 1);
vel ink(kai_size, 1);
vel dist;
int RE() {
	vel v(3, 2);
	return v.at(4);
}
int ru(int a, int r) {
	if (r == 0) { return 1; }
	int ans = ru(a, r / 2);
	ans *= ans; ans %= p;
	if (r % 2 == 1) { ans *= a; }
	return ans % p;
}
int inv(int a) {
	return ru(a, p - 2);
}
void make_kai() {
	rep(i, kai_size) { kai[i + 1] = (kai[i] * (i + 1)) % p; }
	rep(i, kai_size) { ink[i] = inv(kai[i]); }
}
int com(int n, int r) {
	int ans = kai[n] * ink[r];
	ans %= p; ans *= ink[n - r]; ans %= p;
	return ans;
}
int per(int n, int r) {
	int ans = kai[n] * ink[n - r];
	ans %= p;
	return ans;
}
vel dis(int mid1, vvel &way) {
	int n = way.size();
	vel dist(n,-1);
	dist[mid1] = 0;
	queue<int> q;
	q.push(mid1);
	while (!q.empty()) {
		int st = q.front();
		q.pop();
		rep(i, way[st].size()) {
			int to = way[st][i];
			if (dist[to] == -1) {
				dist[to] = dist[st] + 1;
				q.push(to);
			}
		}
	}
	return dist;
}
int gr(int now, vvel &chi) {
	int ret = 0;
	for(auto to:chi[now]) {
		ret = ret xor (gr(to, chi) + 1);
	}
	return ret;
}
signed main() {
	int n; cin >> n;
	vvel way(n);
	vel st(n-1), to(n - 1);
	rep(i, n-1) {
		int x, y; cin >> x >> y;
		x--; y--;
		way[x].push_back(y);
		way[y].push_back(x);
		st[i] = x; to[i] = y;
	}
	vel dist0 = dis(0, way);
	vvel chi(n);
	rep(i, n-1) {
		if (dist0[st[i]] < dist0[to[i]]) {
			chi[st[i]].push_back(to[i]);
		}
		else { chi[to[i]].push_back(st[i]); }
	}
	int ans = gr(0, chi);
	if (ans == 0) { cout << "Bob" << endl; }
	else { cout << "Alice" << endl; }
	return 0;
}

