vector<vector<int> > G;
int calc(int pos, int pre) {
	set<int> s = { 1 };
	for (int i : G[pos]) {
		if (i != pre) {
			s.insert(calc(i, pos));
		}
	}
	int ans = 0;
	while (s.count(ans)) ++ans;
	return ans;
}
