using i64=int64_t;

void solve(){
    i64 N;
    cin >> N;
    vector<vector<i64>> graph(N);
    rep(i,0,N-1){
        int x,y;
        cin >> x >> y;
        --x;
        --y;
        graph[x].push_back(y);
        graph[y].push_back(x);
    }

    function<int(int,int)> rec=[&](int u,int p){
        if(p!=-1 and graph[u].size()==1) return 0;
        int res=0;
        for(auto v:graph[u]){
            if(v==p) continue;
            res^=rec(v,u)+1;
        }
        return res;
    };

    if(rec(0,-1)) cout << "Alice" << endl;
    else cout << "Bob" << endl;
}

int main(){
    std::cin.tie(0);
    std::ios::sync_with_stdio(false);
    cout.setf(ios::fixed);
    cout.precision(16);
    solve();
    return 0;
}

