vector<ll> g[N];
ll d[N];

void dfs(ll u, ll p) {
    vector<ll> v;

    rep(i,0,g[u].size()) {
        ll ch = g[u][i];
        if (ch == p) continue;
        dfs(ch,u);
        v.push_back(d[ch]);
    }

    if (v.size() == 1)
        d[u] = v[0]+1;
    else {
        ll mask = 0;
        rep(i,0,v.size()) {
            mask ^= v[i];
        }
        d[u] = mask;
    }
}

