    rep(i,0,n-1) {
        ll a, b; cin >> a >> b; a--,b--;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    dfs(0,0);
    if (d[0]) {
        cout << "Alice" << endl;
    }
    else {
        cout << "Bob" << endl;
    }
}


