//parentVectornode
//parentVector[root] = -1
//treeRankVectornoderankrootrank0
vector<ll> par;
vector<ll> treeRank;
void makeRootedTree(vector<ll> &parentVector, vector<ll> &treeRankVector, ll vecSize, ll root) {
    parentVector = vector<ll>(vecSize);
    treeRankVector = vector<ll>(vecSize);
    assert(root < (ll)parentVector.size());
    parentVector[root] = -1;
    treeRankVector[root] = 0;
    queue<ll> que;
    que.push(root);
    while (!que.empty()) {
        ll node = que.front();
        que.pop();
        REP (i, 0, G[node].size()) {
            edge e = G[node][i];
            if (e.to != parentVector[node]) {
                parentVector[e.to] = node;
                treeRankVector[e.to] = treeRankVector[node] + 1;
                que.push(e.to);
            }
        }
    }
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);
    ll N;
    cin >> N;
    V = N;
    REP(i, 0, N - 1) {
        ll x, y;
        cin >> x >> y;
        x--;
        y--;
        addedge(x, y, 1);
        addedge(y, x, 1);
    }
    makeRootedTree(par, treeRank, N, 0);
    vector<ll> grundy;
    REP(i, 0, G[0].size()) {
        queue<ll> que;
        vector<ll> rank;
        que.push(G[0][i].to);
        rank.push_back(1);
        while (!que.empty()) {
            ll node = que.front();
            que.pop();
            REP(j, 0, G[node].size()) {
                edge e = G[node][j];
                if (e.to != par[node]) {
                    rank.push_back(treeRank[e.to]);
                    que.push(e.to);
                }
            }
        }
        sort(rank.begin(), rank.end());
        ll tmpgr = 0;
        ll num = rank[0];
        ll tmp = 1;
        REP(i, 1, rank.size()) {
            if (rank[i] != num) {
                if (tmp == 1) {
                    tmpgr++;
                }
                tmp = 1;
                num = rank[i];
            } else {
                tmp++;
            }
        }
        if (tmp == 1) {
            tmpgr++;
        }
        grundy.push_back(tmpgr);
    }
    ll ans = 0;
    REP(i, 0, grundy.size()) {
        ans ^= grundy[i];
    }
    if (ans != 0) {
        cout << "Alice" << endl;
    } else {
        cout << "Bob" << endl;
    }
}

