int main(){
    int N;

    cin >> N;
    vector<vector<int> > adjlists(N);

    for(int i=0;i<N-1;i++){
        int u, v;
        cin >> u >> v;
        u--; v--;

        adjlists[u].push_back(v);
        adjlists[v].push_back(u);
    }
    /*
    for(int i=0;i<N;i++){
        cout << i << ": ";
        for(int j=0;j<adjlists[i].size();j++){
            cout << adjlists[i][j] << " ";
        }
        cout << endl;
    }*/

    // bfs
    queue<int> bfs;
    vector<bool> seen(N, false);
    vector<vector<int> > children(N);
    vector<int> preorder;
    bfs.push(0);
    seen[0] = true;
    while(!bfs.empty()){
        int cur = bfs.front();
        bfs.pop();

        preorder.push_back(cur);

        for(int j=0;j<adjlists[cur].size();j++){
            int nxt = adjlists[cur][j];
            if(!seen[nxt]){
                children[cur].push_back(nxt);
                seen[nxt] = true;
                bfs.push(nxt);
            }
        }
    }


    vector<int> values(N, 0);
    for(int i=N-1;i>=0;i--){
        int cur = preorder[i];
        for(int j=0;j<children[cur].size();j++){
            int child = children[cur][j];
            values[cur] ^= (values[child] + 1);
        }
    }

    if(values[0] == 0) cout << "Bob" << endl;
    else cout << "Alice" << endl;

    return 0;
}

