        oneCnt += ((x[i] == 1) || (y[i] == 1));
        x[i]--;
        y[i]--;
        graph[x[i]].EB(y[i]);
        graph[y[i]].EB(x[i]);
    }
    if (oneCnt == 1) {
        cout << "Alice" << endl;
        return 0;
    }
    if (oneCnt == 2&&false) {
        VI ch;
        FORE(e, graph[0]) ch.EB(dfs(e, 0) + 1);
        cout << (ch[0] % 2 == ch[1] % 2 ? "Bob" : "Alice") << endl;
        return 0;
    }
    else {
        VI ch;
        FORE(e, graph[0]) ch.EB(dfs(e, 0) + 1);
        int odd = 0;
        FORE(e, ch) odd += e % 2;
        cout << (((odd & 1) ^ (oneCnt & 1)) ? "Alice" : "Bob") << endl;
    }
}

