using namespace std;
typedef long long LL;

const int maxn = 200005;
int n,ehead[maxn],ecnt,f[maxn];
struct edge {
	int u,v,next;
}edg[maxn];
void add(int u,int v) {
	edg[++ecnt]=(edge){u,v,ehead[u]};
	ehead[u]=ecnt;
	edg[++ecnt]=(edge){v,u,ehead[v]};
	ehead[v]=ecnt;
}
void solve(int u,int fa)
{
	for (int v,j=ehead[u];j;j=edg[j].next)
	if ((v=edg[j].v)!=fa)
		solve(v,u),f[u]^=f[v]+1;
}
