VV<ll> edges;

ll dfs(ll cur, ll pre) {
    ll ret = 0, cnt_child = 0;
    for(ll nxt : edges[cur]) {
        if(nxt == pre) continue;
        ret ^= dfs(nxt, cur);
        cnt_child++;
    }
    if(cnt_child == 0) return 0;
    if(cnt_child == 1) return ret + 1;
    return ret;
}

