const int MAX_SIZE = 1e5 + 10;

vector<int> graph[MAX_SIZE];
vector<bool> visited(MAX_SIZE, false);

#define pb push_back

ll dfs(int i)
{
	visited[i] = true;
	if(graph[i].size() - 1 == 0)
	{
		return 0;
	}
	else if(graph[i].size() - 1 == 1)
	{
		ll s = 0;
		for(auto &e: graph[i])
		{
			if(visited[e] == false)
			{
				s += dfs(e);
			}
		}
		return 1 + s;
	}
	ll s = 0;
	for(auto &e: graph[i])
	{
		if(!visited[e])
		{
			s ^= dfs(e);
		}
	}
	return s;
}

