	int a, b;
	for (int i = 1; i < N; i++) {
		cin >> a >> b;
		con[a].push_back(b);
		con[b].push_back(a);
	}

	bool seen[100001];
	memset(seen, 0, sizeof(seen));
	q.push({1, 1});
	int d=1, nd, m=-1;
	bool aw = false;
	while (!q.empty()) {
		int p = q.front().second;
		seen[p] = true;
		nd = q.front().first;
		q.pop();
		if (d != nd) {
			if (m %2 == 1) {
				aw = true;
				break;
			}
			d = nd;
			m = 0;
		}
		m++;
		//cout << p << m << "\n";

		for (int i: con[p]) {
			if (seen[i]) continue;
			q.push({d+1, i});
		}
	}
	if (aw or m%2) cout << "Alice";
	else cout << "Bob";

	return 0;
}

