const int MAX_N = 1000005;

class Edge {
public:
  int nxt, to;
} e[MAX_N << 1];

int head[MAX_N], sg[MAX_N], cnt, N;
void addedge(int u, int v) {
  e[++cnt] = (Edge){head[u], v}, head[u] = cnt;
  e[++cnt] = (Edge){head[v], u}, head[v] = cnt;
}

void dfs(int u, int v) {
  for (int i = head[u]; i; i = e[i].nxt)
    if (e[i].to != v) {
      dfs(e[i].to, u);
      sg[u] ^= sg[e[i].to] + 1;
    }
}

