void explore( int root ) {
    explored[ root ] = true;
    int countKid = 0;
    for(const int& nei : g[root] ) {
        if( explored[nei] == false ) {
            countKid++;
            explore(nei);
            node_xor[root] ^= node_xor[nei];
        }
    }
    if( root != 0 ) node_xor[root]++;
}


