        x--;
        y--;
        addedge(x, y, 1);
        addedge(y, x, 1);
    }
    makeRootedTree(par, treeRank, N, 0);
    vector<ll> grundy;
    REP(i, 0, G[0].size()) {
        queue<ll> que;
        vector<ll> rank;
        que.push(G[0][i].to);
        rank.push_back(1);
        while (!que.empty()) {
            ll node = que.front();
            que.pop();
            REP(j, 0, G[node].size()) {
                edge e = G[node][j];
                if (e.to != par[node]) {
                    rank.push_back(treeRank[e.to]);
                    que.push(e.to);
                }
            }
        }
        sort(rank.begin(), rank.end());
        ll tmpgr = 0;
        ll num = rank[0];
        ll tmp = 1;
        REP(j, 1, rank.size()) {
            if (rank[j] != num) {
                if (tmp == 1) {
                    tmpgr++;
                }
                tmp = 1;
                num = rank[j];
            } else {
                tmp++;
            }
        }
        if (tmp == 1) {
            tmpgr++;
        }
        grundy.push_back(tmpgr);
    }
    ll ans = 0;
    REP(i, 0, grundy.size()) {
        ans ^= grundy[i];
    }
    if (ans != 0) {
        cout << "Alice" << endl;
    } else {
        cout << "Bob" << endl;
    }
}

