template <class F>
class fixed_point {
    F f;
    public:
    fixed_point(F&& f_) : f(f_) {}
    template <class... Args> auto operator() (Args... args) { return f(f, args...); }
};
template <class F> auto make_fixed_point(F&& f) { return fixed_point<F>(std::forward<F&&>(f)); }
int main() {
