using ULL = unsigned long long;

ULL P = 1000000007;
ULL seed(int prev, int now)
{
	vector<ULL>cash;
	ULL ret = 1;

	for (int ch : graph[now])
	{
		if (ch == prev)continue;
		cash.push_back(seed(now, ch));
		ret += cash.back();
	}

	sort(cash.begin(),cash.end());

	while (cash.size() > 1 &&
		cash[cash.size() - 1] == cash[cash.size() - 2])
	{
		cash.pop_back();
		cash.pop_back();
	}

	if (cash.empty())
	{
		return 1;
	}

	return ret;
}

