		graph[x].push_back(y);
		graph[y].push_back(x);
	}

	ULL S = seed(-1, 0);



	if (S == 1)
	{
		cout << "Bob" << endl;
	}
	else
	{
		cout << "Alice" << endl;
	}
	return 0;
}

