class solve
{
	long long n;
	ve<bool > vis;
	ve<ve<int> > adj;
	ve<ve<int> > adj2;
	void dfs(int c=0)
	{
		vis[c]=true;
		for(auto &x:adj[c])
		{
			if(!vis[x])
			{
				adj2[c].pb(x);
				dfs(x);
			}
		}
	}
	int Star(int c)
	{
		for(auto &y:adj2[c])
		{
			if(adj2[y].size()!=0)
				return false;
		}
		return true;
	}
	int winner(int c=0)
	{
		vis[c]=false;
		if(adj2[c].size()==1)
			return 1;
		if(adj2[c].size()==0)
			return 0;
		if(Star(c))
		{
			return adj2[c].size()%2;
		}
		int ans=0;
		for(auto &x:adj2[c])
		{
			if(vis[x])
			{
				ans^=winner(x);
			}
		}
		return ans;
	}
