	ull h=1,du=1;
	for(int y:g[x]){
		if(vis[y]) continue;
		Hash.pb(dfs(y));
	}
	if(!Hash.size()){
		h+=1284743ull*du*du*du;
	h^=91348377ull*du;
		return h;
	}
	sort(Hash.begin(),Hash.end());
	for(int i=0;i<Hash.size()-1;i++)
		if(Hash[i]==Hash[i+1]) tag[i]=tag[i+1]=1,i++;
	for(int i=0;i<Hash.size();i++)
		if(!tag[i]) h=h*Hash[i],du++;
	for(int i=0;i<Hash.size();i++) tag[i]=0;
	h+=1284743ull*du*du*du;
	h^=91348377ull*du;
	return h;
}

