set<pair<int, pt> > edges;
char used[N];
int deg;


void dfs(int v, int d = 0){
	used[v] = 1;

	for (auto u : g[v])
		if (!used[u]){
			edges.insert({d, {v, u}});
			dfs(u, d + 1);
		}
}


void del(int v, int p, int d){
	if (p == 0)
		--deg;

	edges.erase({d, {p, v}});

	for (auto u : g[v])
		if (u != p){
			del(u, v, d + 1);
		}
}

void solve(){
	memset(used, 0, sizeof(used));
	edges = set<pair<int, pt> >();
	dfs(0);

	deg = sz(g[0]);
	int st = 1;

	while (!edges.empty() && deg > 0){
		if (deg % 2 == 0){
			auto cur = *edges.rbegin();
			del(cur.y.y, cur.y.x, cur.x);
		}
		else{
			auto cur = *edges.begin();
			del(cur.y.y, cur.y.x, cur.x);
		}

		st ^= 1;
	}

	printf(st ? "Bob\n" : "Alice\n");
}


