int N;
vvi adj;

ll grundy(int v, int p) {
    if (adj[v].size() == 1)
        return 1;
    else {
        int ans = 0;
        for (int u : adj[v]) if (u != p) {
            ans ^= grundy(u, v);
        }
        return ans + (p != -1);
    }
}

