using namespace std;

ll grundy(vector<vector<ll>>& connection, vector<bool>& already, ll now) {
  already.at(now) = true;
  vector<ll> res;
  for (ll i = 0; i < connection.at(now).size(); ++i) {
    ll next = connection.at(now).at(i);
    if (already.at(next)) {
      continue;
    }
    already.at(next) = true;
    res.push_back(grundy(connection, already, next) + 1);
  }
  ll temp = 0;
  for (ll i = 0; i < res.size(); ++i) {
    temp ^= res.at(i);
  }
  // cout << "now" << now << " grundy " << temp << "\n";
  return temp;
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  ll N;
  cin >> N;
