int main(void) {
  cin.tie(0);
  ios::sync_with_stdio(false);

  cin >> N;

  int root = 0;
  int edge = 0;

  rep(i, N - 1) {
      cin >> x[i] >> y[i];
      x[i]--; y[i]--;
      if (x[i] > y[i]) swap(x[i], y[i]);
      if (x[i] == 0) {
          root += 1;
          cand.add(y[i]);
      } 
  }

  rep(i, N) {
    if (HAS(cand, x[i])) {
        edge += 1;
        root -= 1;
        cand.remove(x[i]);
    }
    if (HAS(cand, y[i])) {
        edge += 1;
        root -= 1;
        cand.remove(y[i]);
    }
  }

  int hand = 0;
  while (true) {
    if (edge == 0) {
        root -= 1;
    }
    else {
        if (root%2 == 1) {
            edge -= 1;
        } else {
            root -= 1;
        }
    }
    if (root == 0) {
      cout << (hand == 1 ? "Bob":"Alice") << endl;
      break;
    }

    hand = (hand + 1)%2;
  }
  return 0;
}

