#include <bits/stdc++.h>

#define N 200010
#define M 400010

using namespace std;

#define IV inline void
#define II inline int

#define lc c[x][0]

#define rc c[x][1]

int c[N][2],f[N],s[N],v[N],r[N];

int st[N];

inline bool proot(int x){
	return c[f[x]][0]==x||c[f[x]][1]==x;
}//1

IV pushp(int x){
	s[x]=s[c[x][0]]^s[c[x][1]]^v[x];
}

IV pushr(int x){
	int t=c[x][0];c[x][0]=c[x][1];c[x][1]=t;
	r[x]^=1;return;
}

IV pushdown(int x){//pushdown
	if(r[x])
	{
		if(c[x][0]) pushr(c[x][0]);
		if(c[x][1]) pushr(c[x][1]);
		r[x]=0;
	}
}

II Get(int x){
	return (c[f[x]][1]==x);
}

IV rotate(int x){
	int rf=f[x],rff=f[rf],h=Get(x),rh=Get(rf),w=c[x][!h];
	if(proot(rf)) c[rff][rh]=x;c[x][!h]=rf,c[rf][h]=w;
	if(w)  f[w]=rf;f[rf]=x;f[x]=rff;
	pushp(rf);
	return;
}

IV splay(int x){
	int y=x,z=0;
	st[++z]=y;
	while(proot(y)) st[++z]=y=f[y];
	while(z) pushdown(st[z--]);
	while(proot(x)){
		y=f[x];z=f[y];
		if(proot(y))
		  rotate((c[y][0]==x)^(c[z][0]==y)?x:y);
		rotate(x);
	}
	pushp(x);
}

IV access(int x){
	for(int y=0;x;x=f[y=x])
	  splay(x),c[x][1]=y,pushp(x);
}

IV makeroot(int x){
	access(x);splay(x);
	pushr(x);
}

int findroot(int x){
	access(x);splay(x);
	while(c[x][0]) pushdown(x),x=c[x][0];
	splay(x);
	return x;
}

IV split(int x,int y){
	makeroot(x);
	access(y);splay(y);
}

IV link(int x,int y){
	makeroot(x);
	if(findroot(y)!=x) f[x]=y;
}

IV cut(int x,int y){
	makeroot(x);
	if(findroot(y)==x&&f[y]==x&&!c[y][0]){
		f[y]=c[x][1]=0;
		pushp(x);
	}
}

