#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <string>
#include <algorithm>
using namespace std;
typedef long long LL;

const int MAXN = 2e5 + 5;

int other[MAXN + 5], last[MAXN + 5], pre[MAXN + 5];
int dp[MAXN + 5];
int N, all = -1;

void build(int u, int v)
{
	pre[++all] = last[u];
	last[u] = all;
	other[all] = v;
}

int dfs(int ans, int fa)
{
	int dt = last[ans], c = 0, sum = 0;
	while(dt != -1){
		int dr = other[dt];
		if(dr != fa) c ^= dfs(dr, ans), sum++;
		dt = pre[dt];
	}
	if(ans == 1){
		if(sum == 1) return c ^ 1;
		else return c;
	}
	else return c ^ 1;
}

int main()
{
	scanf("%d", &N);
	memset(last, -1, sizeof(last));
	for(int i = 1; i < N; i++){
		int u, v;
		scanf("%d %d", &u, &v);
		build(u, v);
		build(v, u);
	}
	int check = dfs(1, 0);
	if(check) printf("Alice\n");
	else printf("Bob\n");
}

