class Solve {
public:
    Int N;
    vector<vector<Int>> G;

    pair<Int, Int> dfs(int v, int p = -1) {
        Int ret_l = 1; // is linear ? , 1: linear, 0: not linear
        Int ret_v = 0;
        Int child = 0;

        for (auto e : G[v]) {
            if (e != p) {
                auto tapi = dfs(e, v);
                child++;
                if (tapi.first == 0) {
                    ret_l = 0;
                }
                ret_v ^= tapi.second;
            }
        }
        if (child > 1) {
            ret_l = 0;
        }

        if (child == 0) {
            return make_pair(1ll, 0ll);
        }

        if (ret_l == 1) {
            return make_pair(1ll, ret_v + 1);
        }

        return make_pair(0ll, ret_v);
    }

    void solve() {
        cin >> N;
        G.resize(N);
        for (Int i = 0; i + 1 < N; ++i) {
            Int a, b;
            cin >> a >> b;
            a--, b--;
            G[a].push_back(b);
        }

        auto tapu = dfs(0);
        if (tapu.second == 0) {
            cout << "Bob" << endl;
        } else {
            cout << "Alice" << endl;
        }


    }
};


int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);
    cout << fixed << setprecision(10);

    Solve().solve();


    return 0;
}

